function cameraTakePicture() {
    $("#btnUpload").removeAttr("disabled");
    navigator.camera.getPicture(onSuccess, onFail, {
        quality: 100,
        encodingType: Camera.EncodingType.JPEG,
        //allowEdit: true,
        //correctOrientation: true,
        targetWidth: 250,
        targetHeight: 250,
        destinationType: Camera.DestinationType.DATA_URL
    });

    function onSuccess(imageData) {
        //var image = document.getElementById('myImage');
        var data = "data:image/jpeg;base64," + imageData;
        imgData = imageData;

        //SaveUploadFile(imageData);
    }

    function onFail(message) {
        alert('Failed because: ' + message);
    }
}

function GetFTPDetailsForMAWB() {

    $("#cameraTakePicture").removeAttr("disabled");

    var connectionStatus = navigator.onLine ? 'online' : 'offline'
    var errmsg = "";

    MAWBNo = $('#txtAWBNo').val();

    if (MAWBNo.length != '11') {
        errmsg = "Please enter valid AWB No.";
        $.alert(errmsg);
        return;
    }

    inputxml = 'CON';

    if (errmsg == "" && connectionStatus == "online") {
        $.ajax({
            type: 'POST',
            url: CMSserviceURL + "GetFTPDetails",
            data: JSON.stringify({
                'pi_strCodeo': inputxml,
            }),
            contentType: "application/json; charset=utf-8",
            dataType: "json",
            beforeSend: function doStuff() {
                $('body').mLoading({
                    text: "Loading..",
                });
            },
            success: function (response) {

                console.log(response.d)
                $("body").mLoading('hide');
                var str = response.d;
                if (str != null && str != "") {
                    var xmlDoc = $.parseXML(str);

                    $(xmlDoc).find('Table').each(function (index) {
                        if ($(this).find('Code').text() == 'DOCFTP_HHT_AWBFiles_Folder') {
                            ftpPath = $(this).find('Name').text();
                        }
                        if ($(this).find('Code').text() == 'DOCFTPUser') {
                            ftpUser = $(this).find('Name').text();
                        }
                        if ($(this).find('Code').text() == 'DOCFTPPWD') {
                            ftpPWD = $(this).find('Name').text();
                        }
                        if ($(this).find('Code').text() == 'ISFTPPASSIVEMODE') {
                            ftpIsPassive = $(this).find('Name').text();
                        }

                    });
                }
                else {
                    errmsg = 'Shipment does not exists';
                    $.alert(errmsg);
                }

            },
            error: function (msg) {
                //debugger;
                $("body").mLoading('hide');
                var r = jQuery.parseJSON(msg.responseText);
                $.alert(r.Message);
            }
        });
    }
    else if (connectionStatus == "offline") {
        $("body").mLoading('hide');
        $.alert('No Internet Connection!');
    }
    else if (errmsg != "") {
        $("body").mLoading('hide');
        $.alert(errmsg);
    }
    else {
        $("body").mLoading('hide');
    }
}

function SaveUploadFile(imageData) {

    var connectionStatus = navigator.onLine ? 'online' : 'offline'
    var errmsg = "";

    if (imageData == "") {

        errmsg = "Some error occurred.</br>Please try again.";
        $.alert(errmsg);
        return;

    }

    MAWBNo = $('#txtAWBNo').val();

    if (MAWBNo.length != '11') {
        errmsg = "Please enter valid AWB No.";
        $.alert(errmsg);
        return;
    }

    // inputxml = '<FileList><FileDetails DocName="imgUpload.jpg" UploadedPath="' + ftpPath + '/ED-' + MAWBNo + '" FilePath="" FileName="imgUpload.jpg" FLSize="228227"/></FileList>';
    inputxml = '<FileList><FileDetails DocName="EAWBS_' + MAWBNo + '_imgUpload.jpg" UploadedPath="' + ftpPath + '" FilePath="" FileName="EAWBS_' + MAWBNo + '_imgUpload.jpg" FLSize="228227"/></FileList>';
    console.log(inputxml)
    //imgData = '';

    if (errmsg == "" && connectionStatus == "online") {
        $.ajax({
            type: "POST",
            url: CMSserviceURL + "UploadDocumentsPDA",
            data: JSON.stringify({
                'pi_strImageData': imgData, 'pi_strFTPPath': ftpPath, 'pi_strFTPUser': ftpUser, 'pi_strFTPPwd': ftpPWD, 'pi_intAWBId': '0', 'pi_strFolderPrefix': 'EAWBS',
                'pi_strMawbNo': MAWBNo, 'pi_strHawbNo': '0', 'pi_strFileList': inputxml, 'pi_strRemarks': '', 'pi_strUser': window.localStorage.getItem("UserName"),
            }),
            contentType: "application/json; charset=utf-8",
            dataType: "json",
            beforeSend: function doStuff() {
                //$('.dialog-background').css('display', 'block');
                $('body').mLoading({
                    text: "Please Wait..",
                });
            },
            success: function (response) {

                $("body").mLoading('hide');
                if (response.d != 'True')
                    $.alert('Some error occurred while uploading document');
                else
                    $.alert('Document uploaded successfully.');
            },
            error: function (msg) {
                $("body").mLoading('hide');
                $.alert('Some error occurred while uploading document');
            }
        });
        return false;
    }

}

